<template>	<div class="tabber" :class="[showTabbar?'tabbar-show':'tabbar-hide']" v-show="showTabbar">		<tabber-item v-for="(item,index) in tabbarList" :text="item.text" :path="item.path" :current="current" @chang="getPath" :key="item.path">			<img :src="item.normalImg" alt="" slot="normalImg" />			<img :src="item.activeImg" alt="" slot="activeImg" />		</tabber-item>	</div></template><script>	import TabberItem from './TabberItem'	export default {		name: "TabBar",		data(){			return{				current:'houses',				tabbarList:[					{text:'楼盘',path:'houses',normalImg:require('./img/icon-houses-normal.png'),activeImg:require('./img/icon-houses-active.png')},					{text:'客户',path:'customers',normalImg:require('./img/icon-customer-normal.png'),activeImg:require('./img/icon-customer-active.png')},					{text:'广场',path:'square',normalImg:require('./img/icon-messages-normal.png'),activeImg:require('./img/icon-messages-active.png')},					{text:'我的',path:'user',normalImg:require('./img/icon-user-normal.png'),activeImg:require('./img/icon-user-active.png')}				]			}		},		methods:{			getPath(path){				this.current=path;				console.log(path)			}		},		computed:{			showTabbar(){				return this.$route.path==='/houses' || this.$route.path==='/customers' || this.$route.path==='/square' || this.$route.path==='/user'			}		},		components:{			TabberItem		},		mounted(){			console.log('this.$route',this.$route)		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/tabbar";</style>