<template>	<div class="tabber-item" @click="handleChang" :class="[current===path?'active':'']">		<span v-show="!imgShow">			<slot name="normalImg"></slot>		</span>		<span v-show="imgShow">			<slot name="activeImg"></slot>		</span>		<strong v-text="text"></strong>	</div></template><script>	export default {		name: "TabberItem",		props:{			text:{				type:String,				defalut:''			},			current:{				type:String,				defalut:''			},			path:{				type:String,				defalut:''			}		},		methods:{			handleChang(){				this.$router.push(`/${this.path}`);				this.$emit('chang',this.path);			}		},		computed:{			imgShow(){				return this.current === this.path			}		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/tabbar";</style>